    /**
     * 我的评价
     * @return {[type]} [description]
     */
    function shows(sid){
        $("#caoz"+sid).show();
    }
    function hidd(sid){
        var evaluatename=$("#evaluatename").val();
        var sid = $("#sid").val();
        $.get('evaluate',{'evaluatename':evaluatename,'sid':sid},function(msg){
            if ( msg == '-1' ) {
                alert("评论失败");
            }else
            if ( msg == '-2' ) {
                alert("您输入的内容有广告存在");
            }else
            if ( msg == '-3' ) {
                alert("您是路过还是想坐沙发");
            }else
            if ( msg == '0' ) {
                alert("服务器请求失败");
            }else
            if ( msg == '-4' ) {
                alert("评论失败");
            }else
            if ( msg == '4' ) {
                $("#caoz"+sid).hide();
            }
        })
    }

</script>