            filter_params['type'] = "add";

            var page_like_url = "/personajax/CommonLike";
            $.post(page_like_url, filter_params, function(res) {
                if(res && res.success){
                    $("#cnt_like").html(parseInt(parseInt( $("#cnt_like").text() )) + 1);
                    $("#cnt_like").append(_add);
                }
                else{
                    $("#cnt_like").append(_liked);
                }
                $("#cnt_like").find('span').eq(0).animate({
                    'opacity': 1,
                    'top': '-18px'
                }, 'slow');
                $("#cnt_like").find('span').eq(0).queue(function () {
                    $(this).remove();
                });
                liking = false;
            }, "json");
