        //获取游记列表数据并渲染
    function render_trip_list(year,page){
        //获取当前year参数
        var filter_params = (typeof unique_code_flag != "undefined" && unique_code_flag) ? {page_user_unique_code:page_user_unique_code} : {page_uid:page_uid};
        filter_params['year'] = year;
        filter_params['limit'] = 5;
        filter_params['page'] = page;

        var trip_list_base_url = "/personajax/CommonGetTrips";
        var trip_list_template = _.template( $( "#T_TripList" ).html() );
        $.get(trip_list_base_url, filter_params,
            function(data){
                $("#part_trip .trip_content").remove();
                $("#part_trip .J-Page").remove();
                $("#part_trip").append( trip_list_template( data ) );
            }, "json");
    }
