<script type="text/javascript">
jQuery.fn.passwordStrength = function(options) {
	document.getElementById("password").maxLength=16;
    var element = this;

    ratingMsgs = new Array(4);
    ratingMsgColors = new Array(4);
    barColors = new Array(4);
    ratingMsgs[0] = "太短";
    ratingMsgs[1] = "弱";
    ratingMsgs[2] = "中";
    ratingMsgs[3] = "强";
    
    ratingMsgColors[0] = "#676767";
    ratingMsgColors[1] = "#aa0033";
    ratingMsgColors[2] = "#6699cc";
    ratingMsgColors[3] = "#008000";

    barColors[0] = "#dddddd";
    barColors[1] = "#aa0033";
    barColors[2] = "#6699cc";
    barColors[3] = "#008000";

    /*Add a Span tag to display the strength of the password*/
    $("#password-tip").append("<div id='password-strength'><div id='password-strength-inner'></div></div><span id='rating-msg'></span>");
    
    /*Observe Key Up event display password Strength rating*/
    $(this).live('keyup', function() {
        var pass = $.trim($(this).val());
        var level_score=0;
        var rating=0;
        
        var numericTest = /[0-9]/;
        var lowerCaseAlphaTest = /[a-z]/;
        var upperCaseAlphaTest = /[A-Z]/;
        var symbolsTest = /[.,!@#$%^&*()}{:<>|]/;

        /*Test for the validations*/
        if (numericTest.test(pass)) {
        	level_score=1;
        }
        if (lowerCaseAlphaTest.test(pass)) {
        	level_score=1;
        }
        if (upperCaseAlphaTest.test(pass)) {
        	level_score=1;
        }
        if (symbolsTest.test(pass)) {
        	level_score=2;
        }

        if(pass.length<6){
        	rating=0;
        }
        else if(level_score==1 && pass.length<=9){
        	rating=1;
        }else if(level_score==2 && pass.length<=9){
			rating=2;
		}else if(level_score==1 && pass.length>9){
			rating=2;
		}else if(level_score==2 && pass.length>9){
			rating=3;
		}
		
        //display rating
        $('#password-tip').removeClass('ok');
		$('#password-tip').removeClass('err');
        $('#password-tip .input-tip-inner').hide();
        $('#password-strength').show();
        $('#password-strength-inner').css({"width":(rating/3)*160+"px"});
        $('#rating-msg').text(ratingMsgs[rating]).css({"color":ratingMsgColors[rating]});
        //记录密码强度值
        if(ratingMsgs[rating]=="弱"){
    	document.getElementById('pwd_s').value=1;
        }else if(ratingMsgs[rating]=="中"){
        document.getElementById('pwd_s').value=2;   
        }else if(ratingMsgs[rating]=="强"){
        document.getElementById('pwd_s').value=3;
        }else{
        document.getElementById('pwd_s').value=1;
        }
    });
    
    $(this).live('blur', function() {
        var psw =$("#password").val();
        if (psw === '') {
        	$('#password-tip').removeClass('ok');
			$('#password-tip').addClass('err');
       	 	$('#password-tip .input-tip-inner').html('<p>请输入密码</p>');;
            $('#password-tip .input-tip-inner').show();
            $('#password-strength').hide();
            $('#rating-msg').empty();
        }
        else if(psw.length <6){
        	$('#password-tip').removeClass('ok');
			$('#password-tip').addClass('err');
        	$('#password-tip .input-tip-inner').html('<p>请输入6位以上的密码</p>');;
            $('#password-tip .input-tip-inner').show();
            $('#password-strength').hide();
            $('#rating-msg').empty();
        }
        else if(psw.length>16){
        		$('#password-tip').removeClass('ok');
				$('#password-tip').addClass('err');
            	$('#password-tip .input-tip-inner').html('<p>请输入16位以下的密码</p>');;
                $('#password-tip .input-tip-inner').show();
                $('#password-strength').hide();
                $('#rating-msg').empty();
        }
    });

    /*Observe KeyDown event to clear the result*/
    $(this).live('keydown', function() {
        $('#rating-msg').empty();
    });

    return this;
};


$(document).ready(function(){
    $("#password").passwordStrength();
})  
</script>

