    function imgCropSet(img, set) {
        var $img = $(img).css({
                    width: "auto",
                    height: "auto"
                }),
                $con = $img.parent(),
                width = (img = $img[0]).naturalWidth || $img.width(),
                height = img.naturalHeight || $img.height(),
                ratio, left, top;
        if ($img.attr("data-original") == img.src && set && set.cropData) {
            ratio = Math.max($con.width() / set.cropData.width, $con.height() / set.cropData.height);
            $img.css({
                "width": width * ratio,
                "height": height * ratio,
                "marginLeft": -set.cropData.x * ratio,
                "marginTop": -set.cropData.y * ratio
            });
        } else {
            coverPicLoad($img.attr({
                "data-ow": width,
                "data-oh": height
            })[0], 1);
        }
        $img.css({
            visibility: "visible"
        });
    }
    function imgDisplay(set, $con) {
//            loadImg(set.src,function(){
//                coverPicLoad(this,$con);
//            });
        $('<img data-original="' + (set.src || "") + '" style="visibility:hidden;" data-albumid="' + (set.albumid || "") + '"/>')
                .appendTo($con).load(function () {
                    var img = this, $img = $(img);
                    if (img.src) {
                        imgCropSet(this, set);
                    }
                                    }).lazyload({
                    threshold: 600,
                    skip_invisible: false, //给图片加width 或者设置skip为false  否则chrome下无法加载
                    placeholder: ''
                }).parent().addClass(set.src ? "loading" : "");
        if (set.src) {
            $('<a href="/photo/poi/9595' + (set.albumid ? "_" + set.albumid : "") + '.html" class="imglink" target="_blank"/>').appendTo($con);
        }
    }
