        <script type="text/javascript">
            //方法：paus_slide(),cont_slide()
            //登录页背景图片的暂停和恢复
            //etc.当激活文本框时激活暂停,当不激活文本框时恢复图片切换显示
            //chenjinlong 2011-11-29
            function pause_slide(){
                $('#showIMG').cycle('pause');
            }
            function resume_slide(){
                $("#showIMG").cycle('resume');
            }
            //方法：change_card_tpl(sel_opt)
            //合作卡激活-图片的版权控制 
            //chenjinlong 2011-11-23
     
	 
            
            var gallery = function() {
            }
            
			
            var login = {
                //login tabs
              
			  
                //txt focus
                txtFocus: function() {
                    $('.txt-focus').focus(function(){
                        var input_val = $(this).val();
                        if (input_val==this.defaultValue){
                            $(this).val('');
                            $(this).removeClass('cgrey2');
                        }
                    });
                    $('.txt-focus').blur(function(){
                        var input_val = $(this).val();
                        if (input_val==''){
                            $(this).addClass('cgrey2');
                            $(this).val(this.defaultValue);
                        }
                    });
                },
                gallery: gallery,
                popShow:function() {
                    $('body').append('<div id="overlay"></div>');
                    $('#card-box').show();
                },
                popClose:function() {
                    $('#overlay').remove();
                    $('#card-box').hide();
					if($("#is_login").length>0){
						window.location.href='/u';
						return false;
					}
                },
                pop:function() {
                    var _self = this;
                    $('#J-card').bind('click', function() {
                        _self.popShow();
                    });
                    $('#card-close').bind('click',function() {
                        _self.popClose();
                    });
                },
                int: function(){
                    this.txtFocus();
                    this.pop();
                },
                //切换图片
                changeImg :function(){
                    $("#wait_identify").css("display","");
                    $('#identify_img').attr('src','/identify2.php?flag=100&cache='+Math.random());
                    $("#wait_identify").css("display","none");
                },
                changeImg2 :function(){
                    $("#wait_identify2").css("display","");
                    $('#identify_img2').attr('src','/identify2.php?flag=100&cache='+Math.random());
                    $("#wait_identify2").css("display","none");
                },
                //check name
                checkName : function (){
				
                    resume_slide(); //恢复背景图片切换动作 chenjinlong add 2011-11-29
					
                    $("#line_1").attr("class","line_1");
					
                    $("#login_error").html('');
					
                    var username =  $("#username").val();
				
                    if(username == '' || username == '用户名/手机号/邮箱'){
					
                        this.showErrormsg('请填写用户名/手机号/邮箱');
                        return false;
                    }
                    return true;				
                },
               
			
                //check password
                checkPassword : function (){
                    resume_slide(); //恢复背景图片切换动作 chenjinlong add 2011-11-29
                    $("#line_2").attr("class","line_2");
                    var password = $("#password").val();
                    if(password == ""){
                        this.showErrormsg('请填写密码');
                        return false;
                    }
                    return true;
                },
               
			   
        
		
			   
                //check_identify empty
                checkIdentifyEmpty:function(){
                    $("#line_3").attr("class","line_3");
                    var identify = $("#identify").val();
                    if(identify == ''){
                        this.showErrormsg("请填写验证码");
                        return false;
                    }
                    return  true;
                },
    
           
		   
                //check submit 
                checkData  : function (){
                    var username =$("#username").val();

                    var password =$("#password").val();
                    var identify =$("#identify").val();
                    
			
                    if(!this.checkName()){
                        this.showErrormsg('请填写用户名/手机号/邮箱');
                        return false;
                    }
                    if(!this.checkPassword()){
                        this.showErrormsg('请填写密码');
                        return false;
                    }
                    if(!this.checkIdentifyEmpty()){
                        this.showErrormsg('请填写验证码');
                        return  false;
                    }
                   
				   
               $('#loginFrm').submit();	   
                  	
                },
          
		  
		         changeClass : function(a,b,c){
                 $("#username").val(''); 

                },
		  
            showErrormsg : function (msg){
                $("#login_error").html(msg);
            },
            showErrormsg2 : function (msg){
                $("#login_error2").html(msg);
            }
			
			
        }
       
       
            
    
       
        </script>
      
